import React, { useEffect, useState, useRef } from 'react';
import { useUser } from './UserContext';
import './AsteroidDefenseGame.css';
import { useTranslation } from 'react-i18next';
import { useHighContrast } from './HighContrastContext';

// Simple arcade-style asteroid defense game using real NEO data
const GAME_DURATION = 60; // seconds

function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

// Voice narration for asteroid facts and game events
function speak(text, lang = 'en') {
  if ('speechSynthesis' in window) {
    const utter = new window.SpeechSynthesisUtterance(text);
    utter.lang = lang;
    window.speechSynthesis.speak(utter);
  }
}

const AsteroidDefenseGame = () => {
  const { t, i18n } = useTranslation();
  const { awardBadge, logAction } = useUser();
  const { highContrast } = useHighContrast();
  const [asteroids, setAsteroids] = useState([]);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [timer, setTimer] = useState(GAME_DURATION);
  const [showFact, setShowFact] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const intervalRef = useRef();

  useEffect(() => {
    setLoading(true);
    setError(null);
    const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000';
    fetch(`${backendUrl}/api/neo?start_date=2025-06-18&end_date=2025-06-18`)
      .then(res => res.json())
      .then(data => {
        const neos = Object.values(data.near_earth_objects || {}).flat();
        setAsteroids(neos.slice(0, 10).map(neo => ({
          ...neo,
          x: getRandomInt(10, 90),
          y: 0,
          speed: getRandomInt(1, 3),
          fact: `${neo.name}: ${neo.nasa_jpl_url}`
        })));
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load asteroid data.');
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    if (gameOver) return;
    intervalRef.current = setInterval(() => {
      setAsteroids(asts => asts.map(a => ({ ...a, y: a.y + a.speed })));
      setTimer(t => {
        if (t <= 1) {
          setGameOver(true);
          clearInterval(intervalRef.current);
          logAction('Asteroid Defense Game completed!');
          return 0;
        }
        return t - 1;
      });
    }, 500);
    return () => clearInterval(intervalRef.current);
  }, [gameOver, logAction]);

  useEffect(() => {
    if (showFact) {
      speak(showFact, i18n.language);
    }
  }, [showFact, i18n.language]);

  useEffect(() => {
    if (gameOver) {
      speak(
        t('gameOver', 'Game Over') + '! ' + t('finalScore', 'Final Score') + ': ' + score,
        i18n.language
      );
      if (score > 0) awardBadge('asteroid-hunter');
      logAction('Asteroid Defense Game completed!');
    }
  }, [gameOver, score, t, i18n.language, awardBadge, logAction]);

  return (
    <div className={`asteroid-game${highContrast ? ' high-contrast' : ''}`} aria-label={t('asteroidDefenseGame', 'Asteroid Defense Game')}>
      <h2>{t('asteroidDefenseGame', 'Asteroid Defense Game')}</h2>
      {loading && <div style={{ textAlign: 'center', margin: '1rem' }}>Loading...</div>}
      {error && <div style={{ color: 'salmon', textAlign: 'center', margin: '1rem' }}>{error}</div>}
      {!loading && !error && (
        <>
          <div className="game-area" tabIndex={0} aria-label={t('gameArea', 'Game Area')}>
            {asteroids.map((a) => (
              <div
                key={a.id}
                className="asteroid"
                style={{ left: `${a.x}%`, top: `${a.y}%` }}
                tabIndex={0}
                aria-label={t('asteroid', 'Asteroid') + ' ' + a.name}
                onClick={() => {
                  setScore(s => s + 1);
                  setShowFact(a.fact);
                  setAsteroids(asts => asts.filter(ast => ast.id !== a.id));
                }}
                onKeyDown={e => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    setScore(s => s + 1);
                    setShowFact(a.fact);
                    setAsteroids(asts => asts.filter(ast => ast.id !== a.id));
                  }
                }}
              >
                🪨
              </div>
            ))}
          </div>
          <div className="scoreboard">
            {t('score', 'Score')}: {score} | {t('timeLeft', 'Time Left')}: {timer}s
          </div>
          {showFact && (
            <div className="fact-popup" role="dialog" aria-modal="true">
              <p>{showFact}</p>
              <button onClick={() => setShowFact(null)}>{t('close', 'Close')}</button>
            </div>
          )}
          {gameOver && <div className="game-over">{t('gameOver', 'Game Over')}! {t('finalScore', 'Final Score')}: {score}</div>}
        </>
      )}
    </div>
  );
};

export default AsteroidDefenseGame;
