import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

i18n
  .use(initReactI18next)
  .init({
    resources: {
      en: {
        translation: {
          asteroidDefenseGame: 'Asteroid Defense Game',
          gameArea: 'Game Area',
          asteroid: 'Asteroid',
          score: 'Score',
          timeLeft: 'Time Left',
          close: 'Close',
          gameOver: 'Game Over',
          finalScore: 'Final Score'
        }
      },
      es: {
        translation: {
          asteroidDefenseGame: 'Juego de Defensa de Asteroides',
          gameArea: 'Área de Juego',
          asteroid: 'Asteroide',
          score: 'Puntuación',
          timeLeft: 'Tiempo Restante',
          close: 'Cerrar',
          gameOver: 'Fin del Juego',
          finalScore: 'Puntuación Final'
        }
      }
    },
    lng: 'en',
    fallbackLng: 'en',
    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
