import React, { useRef, useState } from 'react';

const SUGGESTIONS = [
  "What is special about today's APOD?",
  "Tell me about the closest asteroid this week.",
  "What does the latest EPIC image show?",
  "What did Curiosity see on Mars today?"
];

function AskNasaAI({ context, open = true, onClose }) {
  const dialogRef = useRef();
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  if (!open) return null;

  function handleBackdropClick(e) {
    if (dialogRef.current && !dialogRef.current.contains(e.target)) {
      onClose && onClose();
    }
  }

  async function handleAsk() {
    setLoading(true);
    setError(null);
    setAnswer('');
    try {
      // Replace with your actual API endpoint if needed
      const res = await fetch('/api/ask-nasa-ai', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question, context }),
      });
      if (!res.ok) throw new Error('Failed to get answer');
      const data = await res.json();
      setAnswer(data.answer || 'No answer received.');
    } catch (err) {
      setError('Failed to get answer from NASA AI.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      style={{
        position: 'fixed',
        zIndex: 3000,
        left: 0, top: 0, right: 0, bottom: 0,
        background: 'rgba(20,20,40,0.65)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      onMouseDown={handleBackdropClick}
    >
      <div
        ref={dialogRef}
        style={{
          background: '#23243a',
          borderRadius: 14,
          boxShadow: '0 2px 16px #000a',
          padding: 32,
          minWidth: 340,
          maxWidth: '90vw',
          maxHeight: '80vh',
          overflowY: 'auto',
          position: 'relative'
        }}
        onMouseDown={e => e.stopPropagation()}
      >
        <h2 style={{ color: '#ffd700', marginBottom: 16 }}>Ask NASA AI</h2>
        <div style={{ marginBottom: 8 }}>
          {SUGGESTIONS.map(s => (
            <button key={s} onClick={() => setQuestion(s)} style={{ background: '#333', color: '#ffd700', border: 'none', borderRadius: 6, margin: 2, padding: '2px 8px', cursor: 'pointer', fontSize: 12 }}>{s}</button>
          ))}
        </div>
        <textarea
          value={question}
          onChange={e => setQuestion(e.target.value)}
          placeholder="Type your question about space, NASA, or astronomy..."
          style={{
            width: '100%',
            minHeight: 60,
            borderRadius: 8,
            border: '1px solid #ffd70055',
            padding: 10,
            fontSize: 15,
            marginBottom: 12,
            background: '#18182a',
            color: '#fff',
            resize: 'vertical'
          }}
        />
        <button
          onClick={handleAsk}
          disabled={loading || !question.trim()}
          style={{
            background: 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)',
            color: '#23243a',
            border: 'none',
            borderRadius: 8,
            fontWeight: 700,
            fontSize: 15,
            padding: '6px 18px',
            marginBottom: 10,
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Asking...' : 'Ask'}
        </button>
        {error && <div style={{ color: 'salmon', marginBottom: 8 }}>{error}</div>}
        {answer && (
          <div style={{
            background: 'rgba(34,34,60,0.95)',
            color: '#ffd700',
            padding: 16,
            borderRadius: 8,
            marginTop: 10,
            boxShadow: '0 2px 8px #0008',
            maxWidth: 500,
            wordBreak: 'break-word'
          }}>
            {answer}
          </div>
        )}
      </div>
    </div>
  );
}

export default AskNasaAI;
