import React, { useRef, useState, useEffect } from 'react';

function AskNasaAI({ context, open = true, onClose, position }) {
  const dialogRef = useRef();
  const [messages, setMessages] = useState([
    { from: 'bot', text: "ðŸ‘‹ Hi! I'm NASA AI. Ask me anything about space, NASA, or astronomy!" }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Auto-scroll to bottom on new message
  const chatEndRef = useRef();
  useEffect(() => {
    if (chatEndRef.current) chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  if (!open) return null;

  function handleBackdropClick(e) {
    if (dialogRef.current && !dialogRef.current.contains(e.target)) {
      onClose && onClose();
    }
  }

  async function handleSend(e) {
    e.preventDefault();
    const question = input.trim();
    if (!question) return;
    setMessages(msgs => [...msgs, { from: 'user', text: question }]);
    setInput('');
    setLoading(true);
    try {
      const res = await fetch('/api/ask-nasa-ai', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question, context }),
      });
      let answer = 'No answer received.';
      if (res.ok) {
        const data = await res.json();
        answer = data.answer || answer;
      } else {
        answer = 'Failed to get answer from NASA AI.';
      }
      setMessages(msgs => [...msgs, { from: 'bot', text: answer }]);
    } catch {
      setMessages(msgs => [...msgs, { from: 'bot', text: 'Failed to get answer from NASA AI.' }]);
    } finally {
      setLoading(false);
    }
  }

  // Positioning for top left
  const panelStyle = position === 'topleft'
    ? {
        position: 'fixed',
        left: 32,
        top: 150,
        width: 370,
        maxWidth: '95vw',
        height: 420,
        background: 'linear-gradient(135deg, #23243a 80%, #2d2d4a 100%)',
        borderRadius: '18px',
        boxShadow: '0 4px 32px #000b',
        display: 'flex',
        flexDirection: 'column',
        zIndex: 3001
      }
    : {
        position: 'fixed',
        right: 32,
        bottom: 100,
        width: 410,
        maxWidth: '95vw',
        height: 540,
        background: 'linear-gradient(135deg, #23243a 80%, #2d2d4a 100%)',
        borderRadius: '22px',
        boxShadow: '0 4px 32px #000b',
        display: 'flex',
        flexDirection: 'column',
        zIndex: 3001
      };

  return (
    <div
      onMouseDown={handleBackdropClick}
      style={{
        position: 'fixed',
        inset: 0,
        zIndex: 3000,
        background: 'transparent'
      }}
    >
      <div
        ref={dialogRef}
        style={panelStyle}
        onMouseDown={e => e.stopPropagation()}
      >
        {/* Header */}
        <div style={{
          background: 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)',
          color: '#23243a',
          padding: '16px 22px',
          fontWeight: 700,
          fontSize: 20,
          letterSpacing: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          borderTopLeftRadius: position === 'topleft' ? 18 : 22,
          borderTopRightRadius: position === 'topleft' ? 18 : 22,
          borderBottom: '1px solid #ffd70033'
        }}>
          <span>
            <span role="img" aria-label="Chatbot" style={{ fontSize: 22, marginRight: 8 }}>ðŸ’¬âœ¨</span>
            NASA AI Chat
          </span>
          <button
            aria-label="Close"
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              color: '#23243a',
              fontSize: 22,
              fontWeight: 700,
              cursor: 'pointer',
              marginLeft: 8,
              lineHeight: 1
            }}
          >Ã—</button>
        </div>
        {/* Chat messages */}
        <div style={{
          flex: 1,
          padding: '18px 14px 0 14px',
          background: 'transparent',
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          gap: 10
        }}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                alignSelf: msg.from === 'user' ? 'flex-end' : 'flex-start',
                background: msg.from === 'user'
                  ? 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)'
                  : 'rgba(34,34,60,0.97)',
                color: msg.from === 'user' ? '#23243a' : '#ffd700',
                padding: '10px 16px',
                borderRadius: msg.from === 'user' ? '14px 14px 4px 14px' : '14px 14px 14px 4px',
                maxWidth: '80%',
                fontSize: 15,
                boxShadow: '0 2px 8px #0005',
                wordBreak: 'break-word',
                border: msg.from === 'user' ? '1px solid #ffd70055' : '1px solid #ffd70022'
              }}
            >
              {msg.text}
            </div>
          ))}
          {loading && (
            <div style={{
              alignSelf: 'flex-start',
              background: 'rgba(34,34,60,0.97)',
              color: '#ffd700',
              padding: '10px 16px',
              borderRadius: '14px 14px 14px 4px',
              fontSize: 15,
              opacity: 0.85,
              maxWidth: '80%'
            }}>
              <span>
                <span role="img" aria-label="typing" style={{ marginRight: 6 }}>ðŸ¤–</span>
                NASA AI is typing...
              </span>
            </div>
          )}
          <div ref={chatEndRef} />
        </div>
        {/* Input area */}
        <form
          onSubmit={handleSend}
          style={{
            display: 'flex',
            alignItems: 'center',
            padding: '12px 14px',
            background: '#20203a',
            borderBottomLeftRadius: position === 'topleft' ? 18 : 22,
            borderBottomRightRadius: position === 'topleft' ? 18 : 22,
            borderTop: '1px solid #ffd70033',
            gap: 8
          }}
        >
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Type your question..."
            style={{
              flex: 1,
              borderRadius: 8,
              border: '1.5px solid #ffd70077',
              padding: '8px 12px',
              fontSize: 15,
              background: '#23243a',
              color: '#fff',
              outline: 'none',
              boxShadow: '0 1px 4px #0002'
            }}
            disabled={loading}
            autoFocus
          />
          <button
            type="submit"
            disabled={loading || !input.trim()}
            style={{
              background: 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)',
              color: '#23243a',
              border: 'none',
              borderRadius: 8,
              fontWeight: 700,
              fontSize: 15,
              padding: '7px 18px',
              cursor: loading || !input.trim() ? 'not-allowed' : 'pointer',
              transition: 'background 0.2s',
              boxShadow: '0 2px 8px #0003'
            }}
          >
            <span role="img" aria-label="Send" style={{ fontSize: 16, marginRight: 4 }}>ðŸ“¤</span>
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default AskNasaAI;
