import React, { useRef, useState } from 'react';

function AskNasaAI({ context, open = true, onClose }) {
  const dialogRef = useRef();
  const [messages, setMessages] = useState([
    { from: 'bot', text: "Hi! I'm NASA AI. Ask me anything about space, NASA, or astronomy!" }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  if (!open) return null;

  function handleBackdropClick(e) {
    if (dialogRef.current && !dialogRef.current.contains(e.target)) {
      onClose && onClose();
    }
  }

  async function handleSend(e) {
    e.preventDefault();
    const question = input.trim();
    if (!question) return;
    setMessages(msgs => [...msgs, { from: 'user', text: question }]);
    setInput('');
    setLoading(true);
    try {
      // Replace with your actual API endpoint if needed
      const res = await fetch('/api/ask-nasa-ai', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question, context }),
      });
      let answer = 'No answer received.';
      if (res.ok) {
        const data = await res.json();
        answer = data.answer || answer;
      } else {
        answer = 'Failed to get answer from NASA AI.';
      }
      setMessages(msgs => [...msgs, { from: 'bot', text: answer }]);
    } catch {
      setMessages(msgs => [...msgs, { from: 'bot', text: 'Failed to get answer from NASA AI.' }]);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div
      style={{
        position: 'fixed',
        zIndex: 3000,
        left: 0, top: 0, right: 0, bottom: 0,
        background: 'rgba(20,20,40,0.65)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      onMouseDown={handleBackdropClick}
    >
      <div
        ref={dialogRef}
        style={{
          background: '#18182a',
          borderRadius: 18,
          boxShadow: '0 4px 32px #000a',
          padding: 0,
          minWidth: 370,
          maxWidth: '95vw',
          width: 400,
          maxHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
          position: 'relative'
        }}
        onMouseDown={e => e.stopPropagation()}
      >
        {/* Header */}
        <div style={{
          background: 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)',
          color: '#23243a',
          padding: '18px 24px',
          fontWeight: 700,
          fontSize: 20,
          letterSpacing: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}>
          <span>NASA AI Chat</span>
          <button
            aria-label="Close"
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              color: '#23243a',
              fontSize: 22,
              fontWeight: 700,
              cursor: 'pointer',
              marginLeft: 8
            }}
          >Ã—</button>
        </div>
        {/* Chat messages */}
        <div style={{
          flex: 1,
          padding: '18px 18px 0 18px',
          background: '#18182a',
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          gap: 10
        }}>
          {messages.map((msg, idx) => (
            <div
              key={idx}
              style={{
                alignSelf: msg.from === 'user' ? 'flex-end' : 'flex-start',
                background: msg.from === 'user'
                  ? 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)'
                  : 'rgba(34,34,60,0.95)',
                color: msg.from === 'user' ? '#23243a' : '#ffd700',
                padding: '10px 16px',
                borderRadius: msg.from === 'user' ? '16px 16px 4px 16px' : '16px 16px 16px 4px',
                maxWidth: '80%',
                fontSize: 15,
                boxShadow: '0 2px 8px #0005'
              }}
            >
              {msg.text}
            </div>
          ))}
          {loading && (
            <div style={{
              alignSelf: 'flex-start',
              background: 'rgba(34,34,60,0.95)',
              color: '#ffd700',
              padding: '10px 16px',
              borderRadius: '16px 16px 16px 4px',
              fontSize: 15,
              opacity: 0.8,
              maxWidth: '80%'
            }}>
              <span>NASA AI is typing...</span>
            </div>
          )}
        </div>
        {/* Input area */}
        <form
          onSubmit={handleSend}
          style={{
            display: 'flex',
            alignItems: 'center',
            padding: '14px 18px',
            background: '#20203a',
            borderTop: '1px solid #ffd70033',
            gap: 8
          }}
        >
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Type your question..."
            style={{
              flex: 1,
              borderRadius: 8,
              border: '1px solid #ffd70055',
              padding: '8px 12px',
              fontSize: 15,
              background: '#23243a',
              color: '#fff',
              outline: 'none'
            }}
            disabled={loading}
            autoFocus
          />
          <button
            type="submit"
            disabled={loading || !input.trim()}
            style={{
              background: 'linear-gradient(90deg, #ffd700 60%, #ffb347 100%)',
              color: '#23243a',
              border: 'none',
              borderRadius: 8,
              fontWeight: 700,
              fontSize: 15,
              padding: '7px 18px',
              cursor: loading || !input.trim() ? 'not-allowed' : 'pointer',
              transition: 'background 0.2s'
            }}
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default AskNasaAI;
